var Layout;

require('./_prepare');

Layout = mod('Layout');

describe("constructor()");

it("should create root block", function() {
  var l;
  l = new Layout;
  expect(l._root).to.exist;
  return l._root._name.should.equal('__root');
});

describe("get()");

it("should not be allowed when any block is open", function() {
  var l;
  l = new Layout;
  l.openBlock();
  return (function() {
    return l.get();
  }).should["throw"](Error);
});
